draw(graph(tan,0,1.3),red);
draw(graph(tan,pi - 1.3,1.3 + pi),red);
draw(graph(tan,2*pi - 1.3,2*pi),red);
draw((0,tan(-1.3))--(0,tan(1.3)));
draw((pi/2,tan(-1.3))--(pi/2,tan(1.3)),dashed);
draw((3*pi/2,tan(-1.3))--(3*pi/2,tan(1.3)),dashed);
draw((0,0)--(2*pi,0));
draw((pi,0.2)--(pi,-0.2));
